import '../App.css'
import React, {useState} from 'react'
import skills from './Sinfo'
export default function Skills(){
    const [isFocused, setIsFocused] = useState(false);
    const handleFocus = () => setIsFocused(true);
    const handleBlur = () => setIsFocused(false);
    return(
        <div className='skillsection'>
            <b style={{fontSize:'10vh'}}>Skills</b><hr/>
            <section>
                {skills.map((instance)=>
                    <div style={{width:'clamp(500px,50%,720px)'}}>
                        <h1>{instance.name}</h1>
                        <p className='description'>{instance.des}</p><br/>
                        <p style={{fontWeight:'700',marginBottom:'1vh'}}>Self Evalution:</p>
                        <div className="progress-bar">
                            <div className="progress-fill" style={{width: isFocused ?`${instance.progress}%`:0}} onFocus={handleFocus}
                                onBlur={handleBlur}></div><p>{instance.progress}%</p>
                        </div>
                        <br />
                        <br />
                    </div>
                )}
            </section>
        </div>
    )
}
