import React, { useState, useEffect, useRef } from 'react';
import skills from './Sinfo';
import '../App.css';

export default function Skills() {
  const [isFocused, setIsFocused] = useState(false);

  const handleFocus = () => setIsFocused(true);
  const handleBlur = () => setIsFocused(false);

  // Refs for each progress bar element (one per skill)
  const progressBarRefs = useRef([]);

  useEffect(() => {
    const observers = [];

    skills.forEach((instance, index) => {
      const observer = new IntersectionObserver((entries) => {
        const [entry] = entries;
        if (entry.isIntersecting) {
          // Progress bar is visible, set width dynamically
          progressBarRefs.current[index].style.width = `${instance.progress}%`;
          observers[index].disconnect(); // Cleanup after visibility is detected
        }
      });

      observer.observe(progressBarRefs.current[index]);
      observers.push(observer);
    });

    return () => observers.forEach((observer) => observer.disconnect()); // Global cleanup
  }, [skills]); // Re-run useEffect when skills data changes

  return (
    <div className="skillsection">
      <b><span style={{ fontSize: '10vh' }}>Skills</span></b>
      <hr />
      <section>
        {skills.map((instance, index) => (
          <div key={instance.name} style={{ width: 'clamp(500px, 50%, 720px)' }}>
            <h1>{instance.name}</h1>
            <p className="description">{instance.des}</p>
            <br />
            <p style={{ fontWeight: '700', marginBottom: '1vh' }}>Self Evalution:</p>
            <div className="progress-bar">
              <div
                className="progress-fill"
                ref={(el) => (progressBarRefs.current[index] = el)}
                style={{ width: isFocused ? `${instance.progress}%` : 0 }} // Initial width (optional)
                onFocus={handleFocus}
                onBlur={handleBlur}
              />
              <p>{instance.progress}%</p>
            </div>
            <br />
            <br />
          </div>
        ))}
      </section>
    </div>
  );
}
